        if (!coords) {
            throw new Error(`Stadt "${city}" nicht gefunden.`);
        }

        // 2. Fetch Weather Data
        const weatherData = await this.getWeatherData(coords.latitude, coords.longitude);

        // 3. Update UI
        this.updateUI(weatherData, coords.name, coords.country);

        // Show container
        document.getElementById('weather-container').style.display = 'block';

    } catch (error) {
        console.error(error);
        this.showError(error.message);
        document.getElementById('weather-container').style.display = 'none';
    } finally {
        this.showLoading(false);
    }
  },

  async getCoordinates(city) {
      const url = `${config.GEOCODING_API_URL}?name=${encodeURIComponent(city)}&count=1&language=de&format=json`;
      const response = await fetch(url);
      if (!response.ok) throw new Error('Geocoding Failed');
      const data = await response.json();

      if (data.results && data.results.length > 0) {
          return data.results[0]; // { name, latitude, longitude, country, ... }
      }
      return null;
  },

  async getWeatherData(lat, lon) {
      const params = new URLSearchParams({
          latitude: lat,
          longitude: lon,
          current: 'temperature_2m,relative_humidity_2m,apparent_temperature,precipitation_probability,weather_code,wind_speed_10m',
          hourly: 'temperature_2m,weather_code,precipitation_probability',
          timezone: 'auto',
          forecast_days: 1
      });

      const url = `${config.API_URL}/forecast?${params.toString()}`;
      const response = await fetch(url);
      if (!response.ok) throw new Error('Weather Data Fetch Failed');
      return await response.json();
  },

  updateUI(data, cityName, country) {
      const current = data.current;
      const hourly = data.hourly;

      // -- Header --
      document.getElementById('city-name').textContent = `${cityName}${country ? ', ' + country : ''}`;
      // Datum formatieren
      const date = new Date();
      const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
      document.getElementById('current-date').textContent = date.toLocaleDateString('de-DE', options);

      // -- Current Weather --
      document.getElementById('current-temp').textContent = Math.round(current.temperature_2m);
      document.getElementById('apparent-temp').textContent = `${Math.round(current.apparent_temperature)}¬∞C`;
      document.getElementById('humidity').textContent = `${current.relative_humidity_2m}%`;
      document.getElementById('wind-speed').textContent = `${current.wind_speed_10m} km/h`;
      document.getElementById('precipitation-prob').textContent = `${current.precipitation_probability}%`;

      // Weather Description & Icon
      const codeInfo = this.getWeatherInfo(current.weather_code);
      document.getElementById('weather-desc').textContent = codeInfo.description;
      document.getElementById('weather-icon').textContent = codeInfo.icon;

      // -- Hourly Forecast --
      this.renderHourlyForecast(hourly);
  },

  renderHourlyForecast(hourlyData) {
      const listContainer = document.getElementById('hourly-list');
      listContainer.innerHTML = ''; // Clear previous

      const currentHour = new Date().getHours();

      // Open-Meteo returns array of times. We only want from current hour onwards
      // The arrays are parallel (time, temperature_2m, etc.)

      for (let i = 0; i < hourlyData.time.length; i++) {
          const timeStr = hourlyData.time[i]; // ISO string like "2023-10-27T00:00"
          const dateObj = new Date(timeStr);
          const hour = dateObj.getHours();

          // Nur Zukunft/Jetzt anzeigen
          if (hour >= currentHour) {
              const temp = Math.round(hourlyData.temperature_2m[i]);
              const code = hourlyData.weather_code[i];
              const codeInfo = this.getWeatherInfo(code);

              const item = document.createElement('div');
              item.className = 'hourly-item';
              item.innerHTML = `
                  <div class="hour">${hour}:00</div>
                  <div class="icon">${codeInfo.icon}</div>
                  <div class="temp">${temp}¬∞C</div>
              `;
              listContainer.appendChild(item);
          }
      }
  },

  getWeatherInfo(wmoCode) {
      // https://open-meteo.com/en/docs
      const codes = {
          0: { description: 'Klar', icon: '‚òÄÔ∏è' },
          1: { description: '√úberwiegend klar', icon: 'üå§Ô∏è' },
          2: { description: 'Teils bew√∂lkt', icon: '‚õÖ' },
          3: { description: 'Bew√∂lkt', icon: '‚òÅÔ∏è' },
          45: { description: 'Nebel', icon: 'üå´Ô∏è' },
          48: { description: 'Nebel mit Reif', icon: 'üå´Ô∏è' },
          51: { description: 'Leichter Nieselregen', icon: 'üå¶Ô∏è' },
          53: { description: 'Nieselregen', icon: 'üå¶Ô∏è' },
          55: { description: 'Starker Nieselregen', icon: 'üåßÔ∏è' },
          61: { description: 'Leichter Regen', icon: 'üå¶Ô∏è' },
          63: { description: 'Regen', icon: 'üåßÔ∏è' },
          65: { description: 'Starker Regen', icon: 'üåßÔ∏è' },
          80: { description: 'Regenschauer', icon: 'üå¶Ô∏è' },
          81: { description: 'Starke Regenschauer', icon: 'üåßÔ∏è' },
          95: { description: 'Gewitter', icon: '‚ö°' },
          96: { description: 'Gewitter mit Hagel', icon: '‚õàÔ∏è' },
          99: { description: 'Starkes Gewitter', icon: '‚õàÔ∏è' },
      };
      // Fallback f√ºr Schnee etc. vereinfacht
      if (wmoCode >= 71 && wmoCode <= 77) return { description: 'Schneefall', icon: '‚ùÑÔ∏è' };
      if (wmoCode >= 85 && wmoCode <= 86) return { description: 'Schneeschauer', icon: '‚ùÑÔ∏è' };

      return codes[wmoCode] || { description: 'Unbekannt', icon: '‚ùì' };
  },

  showLoading(isLoading) {
      const el = document.getElementById('loading');
      if(el) el.style.display = isLoading ? 'block' : 'none';
  },

  showError(msg) {
      const el = document.getElementById('error-message');
      if (el) {
          el.textContent = msg || '';
          el.style.display = msg ? 'block' : 'none';
      }

