    padding: 12px 15px;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    outline: none;
    background: transparent;
}

button {
    padding: 10px 20px;
    background: var(--primary-color);
    color: white;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: background 0.2s ease;
}

button:hover {
    background: var(--primary-hover);
}

/* Main Content */
#weather-container {
    animation: fadeIn 0.5s ease-out;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

/* Current Weather */
.current-weather-card {
    text-align: center;
    margin-bottom: 2.5rem;
    padding: 0 1rem;
}

.location-header h2 {
    font-size: 1.8rem;
    margin-bottom: 0.25rem;
    font-weight: 700;
}

.location-header p {
    color: var(--text-muted);
    font-size: 0.95rem;
    margin-bottom: 1.5rem;
}

.temp-overview {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
}

.large-icon {
    font-size: 5rem;
    line-height: 1;
    margin-bottom: 0.5rem;
    filter: drop-shadow(0 4px 6px rgba(0,0,0,0.1));
}

.temp-display {
    font-size: 4rem;
    font-weight: 800;
    color: var(--text-color);
    line-height: 1;
    position: relative;
    margin-left: 15px; /* Offset for unit */
}

.unit {
    font-size: 1.5rem;
    position: absolute;
    top: 5px;
    right: -25px;
    font-weight: 500;
    color: var(--text-muted);
}

#weather-desc {
    font-size: 1.25rem;
    color: var(--text-muted);
    font-weight: 500;
}

/* Details Grid */
.details-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 15px;
    margin-bottom: 2.5rem;
}

.detail-item {
    background: var(--item-bg);
    padding: 15px;
    border-radius: 16px;
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 8px;
    transition: transform 0.2s;
}

.detail-item:hover {
    transform: translateY(-2px);
}

.detail-item .icon {
    font-size: 1.5rem;
}

.detail-item .label {
    font-size: 0.8rem;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    font-weight: 600;
}

.detail-item .value {
    font-size: 1.1rem;
    font-weight: 700;
    color: var(--text-color);
}

/* Hourly Forecast */
.hourly-forecast-container h3 {
    font-size: 1.1rem;
    margin-bottom: 1rem;
    color: var(--text-color);
    padding-left: 5px;
}

.hourly-list {
    display: flex;
    gap: 12px;
    overflow-x: auto;
    padding: 10px 5px 20px 5px;
    scrollbar-width: thin;
    -webkit-overflow-scrolling: touch;
}

.hourly-list::-webkit-scrollbar {
    height: 4px;
}

.hourly-list::-webkit-scrollbar-thumb {
    background: #cbd5e0;
    border-radius: 2px;
}

.hourly-item {
    min-width: 70px;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 12px 10px;
    background: var(--item-bg);
    border-radius: 14px;
    gap: 8px;
    flex-shrink: 0;
}

.hourly-item .hour {
    font-size: 0.85rem;
    color: var(--text-muted);
}

.hourly-item .icon {
    font-size: 1.4rem;
}

.hourly-item .temp {
    font-weight: 700;
    font-size: 1rem;
}

/* States */
#loading {
    text-align: center;
    padding: 2rem;
    color: var(--text-muted);
    font-weight: 500;
}

#error-message {
    margin-top: 1rem;
    padding: 1rem;
    background: #fff5f5;
    border: 1px solid #feb2b2;
    border-radius: 12px;
    color: #c53030;
    text-align: center;
    font-size: 0.9rem;
}

@media (max-width: 480px) {
    body {
        padding: 0;
        align-items: flex-start;
    }

    #app {
        border-radius: 0;
        min-height: 100vh;
        box-shadow: none;
    }
