 * Format a Date or string into YYYY-MM-DD.
 */
function formatDateToYMD(dateInput) {
  const date = typeof dateInput === "string" ? new Date(dateInput) : dateInput;
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
}

/**
 * Perform a simple fetch and parse JSON with error handling.
 */
async function fetchJson(url) {
  const response = await fetch(url);
  if (!response.ok) {
    throw new Error(`Request failed: ${response.status} ${response.statusText}`);
  }
  return await response.json();
}

/**
 * Geocode a city name to latitude/longitude using Open-Meteo's geocoding API.
 */
export async function geocodeCity(cityName) {
  const encoded = encodeURIComponent(cityName);
  const url = `${GEOCODING_BASE}?name=${encoded}&count=1`;
  const data = await fetchJson(url);
  if (!data || !data.results || data.results.length === 0) {
    throw new Error("City not found");
  }
  const top = data.results[0];
  return {
    latitude: top.latitude,
    longitude: top.longitude,
    name: top.name,
    country: top.country,
  };
}

/**
 * Get current weather for a city.
 */
export async function getCurrentWeather(cityName) {
  const location = await geocodeCity(cityName);
  const url = `${OPEN_METEO_BASE}?latitude=${location.latitude}&longitude=${location.longitude}&current_weather=true&timezone=auto`;
  const data = await fetchJson(url);
  return {
    location,
    current: data.current_weather,
  };
}

/**
 * Get hourly forecast for a city.
 */
export async function getHourlyForecast(cityName, options = {}) {
  const {
    hourly = ["temperature_2m"],
    startDate = new Date(),
    endDate = new Date(new Date().getTime() + 24 * 60 * 60 * 1000),
  } = options;

  const location = await geocodeCity(cityName);
  const start = formatDateToYMD(startDate);
  const end = formatDateToYMD(endDate);
  const hourlyParam = hourly.join(",");
  const url = `${OPEN_METEO_BASE}?latitude=${location.latitude}&longitude=${location.longitude}&hourly=${hourlyParam}&start_date=${start}&end_date=${end}&timezone=auto`;
  const data = await fetchJson(url);
  return {
    location,
    hourly: data.hourly,
  };
}

/**
 * Fetch hourly data for a city for a given list of hourly variables.
 */
export async function getHourlyByVariables(cityName, variables = [], options = {}) {
  const vars = Array.isArray(variables) ? variables : [variables];
  if (vars.length === 0) {
    throw new Error("No hourly variables requested");
  }
  return await getHourlyForecast(cityName, { ...options, hourly: vars });
}

/**
 * Convenience: fetch common hourly groups by type.
 */
export async function getHourlyByType(cityName, type, options = {}) {
  const map = {
    rain: ["precipitation", "rain", "showers", "precipitation_probability"],
    sun: ["shortwave_radiation", "is_day"],
    snow: ["snowfall", "snow_depth"],
    temp: ["temperature_2m", "apparent_temperature"],
    wind: ["wind_speed_10m", "wind_gusts_10m", "wind_direction_10m"],
  };

  const variables = map[type] || [type];
  return await getHourlyByVariables(cityName, variables, options);
}

/**
 * Get daily forecast for a city.
 */
export async function getDailyForecast(cityName, options = {}) {
  const {
    daily = ["temperature_2m_max", "temperature_2m_min", "precipitation_sum"],
    hourly = [
      "temperature_2m",
      "precipitation",
      "snowfall",
      "rain",
      "showers",
      "weather_code",
      "shortwave_radiation",
      "wind_speed_10m",
    ],
    startDate = new Date(),
    endDate = new Date(new Date().getTime() + 7 * 24 * 60 * 60 * 1000),
  } = options;

  const location = await geocodeCity(cityName);
  const start = formatDateToYMD(startDate);
  const end = formatDateToYMD(endDate);
  const dailyParam = daily.join(",");
  const hourlyParam = hourly.join(",");
  const url = `${OPEN_METEO_BASE}?latitude=${location.latitude}&longitude=${location.longitude}&daily=${dailyParam}&hourly=${hourlyParam}&start_date=${start}&end_date=${end}&timezone=auto`;
  const data = await fetchJson(url);
  return {
    location,
    daily: data.daily,
    hourly: data.hourly,
  };
}

export default {
  geocodeCity,
  getCurrentWeather,
  getHourlyForecast,
  getDailyForecast,
};

import OpenMeteo from "./openMeteo.js";

const app = {
  async init() {
    this.cache = {};

    const form = document.getElementById("search-form");
    const input = document.getElementById("city-input");

    form.addEventListener("submit", async (ev) => {
      ev.preventDefault();
      const city = input.value.trim();
      if (!city) return;
      await this.handleSearch(city);
    });

    // Tab click handling (loads a module for each tab)
    document.querySelectorAll(".tab-item").forEach((el) => {
      el.addEventListener("click", (e) => {
        const tab = e.currentTarget.dataset.tab;
        this.switchTab(tab);
      });
    });
  },

  async handleSearch(city) {
    try {
      this.setRawOutput("Loading...");

      // Fetch only current weather and location. Hourly/daily data fetched per-tab.
      const currentResp = await OpenMeteo.getCurrentWeather(city);
      this.currentCity = city;

      const combined = {
        city,
        current: currentResp.current,
        location: currentResp.location,
      };

      this.cache[city.toLowerCase()] = combined;
      this.displayWeather(combined);
    } catch (err) {
      this.setRawOutput(`Error: ${err.message}`);
      console.error(err);
    }
  },

  async switchTab(tabName) {
    // UI switching
    this.currentTab = tabName;
    document.querySelectorAll(".tab-item").forEach((el) => el.classList.toggle("active", el.dataset.tab === tabName));
    document.querySelectorAll(".tab-panel").forEach((panel) => {
      panel.hidden = panel.dataset.tabPanel !== tabName;
    });

    // Load the tab-specific module and run its load() method
    const panel = document.querySelector(`.tab-panel[data-tab-panel="${tabName}"]`);
    if (panel) {
      const contentEl = panel.querySelector("div") || panel;
      contentEl.textContent = "Loading...";
      try {
        const mod = await import(`./tabs/${tabName}.js`);
        if (mod && typeof mod.load === "function") {
          await mod.load(this.currentCity);
        } else {
          contentEl.textContent = "No module handler for this tab.";
        }
      } catch (err) {
        contentEl.textContent = `Error loading tab: ${err.message}`;
        console.error(err);
      }
    }
  },

  displayWeather(data) {
    // Simple population of the panels; placeholder for specific metric logic
    const raw = document.getElementById("raw-output");
    const tempContent = document.getElementById("temp-content");
    const rainContent = document.getElementById("rain-content");
    const sunContent = document.getElementById("sun-content");
    const snowContent = document.getElementById("snow-content");

    raw.textContent = JSON.stringify(data, null, 2);

    // Temp panel: show current temperature and unit
    if (data.current) {
      tempContent.textContent = `Current: ${data.current.temperature}° (wind ${data.current.windspeed} km/h)`;
    } else {
      tempContent.textContent = "No current data";
    }

    // Rain/sun/snow placeholders — expand with daily/hourly arrays later
    rainContent.textContent = "Rain metrics will be shown here.";
    sunContent.textContent = "Sun metrics will be shown here.";
    snowContent.textContent = "Snow metrics will be shown here.";

    // Ensure default active tab
    this.switchTab("temp");
  },

  setRawOutput(text) {
    const raw = document.getElementById("raw-output");
    raw.textContent = text;
  },
};

// Start app when DOM is loaded
document.addEventListener("DOMContentLoaded", () => {
  app.init();
});

/**
 * Rain tab logic
 */
import OpenMeteo from "../openMeteo.js";

/**
 * Load rainfall metrics for the given city and render into the rain panel.
 */
export async function load(city) {
  const el = document.getElementById("rain-content");
  if (!city) {
    el.textContent = "Enter a city to see rain data.";
    return null;
  }

  el.textContent = "Loading rain data...";
  const data = await OpenMeteo.getHourlyByType(city, "rain", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });

  if (!data || !data.hourly) {
    el.textContent = "No rain data available.";
    return data;
  }

  const dates = data.hourly.time || [];
  const precip = data.hourly.precipitation || [];

  const lines = dates.map((d, i) => `${d}: ${precip[i] ?? 0} mm`);
  el.textContent = lines.join("\n");
  return data;
}

export default { load };

/**
 * Temp tab logic
 */
import OpenMeteo from "../openMeteo.js";

/**
 * Load temperature metrics for the given city and render into the temp panel.
 */
export async function load(city) {
  const el = document.getElementById("temp-content");
  if (!city) {
    el.textContent = "Enter a city to see temperature data.";
    return null;
  }

  el.textContent = "Loading temperature data...";
  const data = await OpenMeteo.getHourlyByType(city, "temp", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });

  if (!data || !data.hourly) {
    el.textContent = "No temperature data available.";
    return data;
  }

  const dates = data.hourly.time || [];
  const temps = data.hourly.temperature_2m || [];

  const lines = dates.map((d, i) => `${d}: ${temps[i]}°`);
  el.textContent = lines.join("\n");
  return data;
}

export default { load };

/**
 * Sun tab logic
 */
import OpenMeteo from "../openMeteo.js";

/**
 * Load solar metrics (sunrise/sunset) for the given city and render into the sun panel.
 */
export async function load(city) {
  const el = document.getElementById("sun-content");
  if (!city) {
    el.textContent = "Enter a city to see sun data.";
    return null;
  }

  el.textContent = "Loading sun data...";
  // For sun, combine daily sunrise/sunset and shortwave hourly radiation
  const [dailyData, hourlyData] = await Promise.all([
    OpenMeteo.getDailyForecast(city, { daily: ["sunrise", "sunset", "sunshine_duration"] }),
    OpenMeteo.getHourlyByType(city, "sun", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) }),
  ]);

  if (!dailyData || !dailyData.daily) {
    el.textContent = "No sun data available.";
    return dailyData;
  }

  const dates = dailyData.daily.time || [];
  const sunrise = dailyData.daily.sunrise || [];
  const sunset = dailyData.daily.sunset || [];

  const dailyLines = dates.map((d, i) => `${d}: sunrise ${sunrise[i]}  •  sunset ${sunset[i]}`);

  const hourlyLines = [];
  if (hourlyData && hourlyData.hourly) {
    const times = hourlyData.hourly.time || [];
    const rad = hourlyData.hourly.shortwave_radiation || [];
    times.forEach((t, i) => hourlyLines.push(`${t}: ${rad[i] ?? 0} W/m²`));
  }

  el.textContent = dailyLines.join("\n") + "\n\nHourly radiation:\n" + hourlyLines.join("\n");
  return { daily: dailyData.daily, hourly: hourlyData && hourlyData.hourly };
}

export default { load };

/**
 * Snow tab logic
 */
import OpenMeteo from "../openMeteo.js";

/**
 * Load snowfall metrics for the given city and render into the snow panel.
 */
export async function load(city) {
  const el = document.getElementById("snow-content");
  if (!city) {
    el.textContent = "Enter a city to see snow data.";
    return null;
  }

  el.textContent = "Loading snow data...";
  const data = await OpenMeteo.getHourlyByType(city, "snow", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });

  if (!data || !data.hourly) {
    el.textContent = "No snow data available.";
    return data;
  }

  const dates = data.hourly.time || [];
  const snow = data.hourly.snowfall || [];

  const lines = dates.map((d, i) => `${d}: ${snow[i] ?? 0}`);
  el.textContent = lines.join("\n");
  return data;
}

export default { load };

