/**
 * Very small, dependency-free chart helpers for line, bar, area and heatmap.
 * Each factory returns { update(data), destroy() }.
 */

function clearCanvas(canvas) {
  const ctx = canvas.getContext("2d");
  ctx.clearRect(0, 0, canvas.width, canvas.height);
}

function scaleValues(values, height, padding = 20) {
  const numeric = values.map((v) => (v == null || Number.isNaN(Number(v)) ? 0 : Number(v)));
  const min = Math.min(...numeric);
  const max = Math.max(...numeric);
  const range = max - min || 1;
  return numeric.map((v) => {
    return padding + (1 - (v - min) / range) * (height - padding * 2);
  });
}

function normalizeSeries(labels = [], values = [], maxPoints = 500) {
  const n = Math.min(Math.max(labels.length, values.length), maxPoints);
  if (n === 0) return { labels: [], series: [] };
  // if input longer than maxPoints, sample
  const step = Math.max(1, Math.floor(Math.max(labels.length, values.length) / n));
  const outLabels = [];
  const outSeries = [];
  for (let i = 0; i < Math.max(labels.length, values.length); i += step) {
    outLabels.push(labels[i] ?? ``);
    outSeries.push(values[i] == null ? 0 : Number(values[i]));
  }
  return { labels: outLabels, series: outSeries };
}

export function createLineChart(canvas, opts = {}) {
  const ctx = canvas.getContext("2d");
  const { strokeStyle = "#3498db", grid = true, title = "", yLabel = "", xLabel = "", maxPoints = 500 } = opts;

  function draw(labels = [], values = []) {
    clearCanvas(canvas);
    const w = canvas.width;
    const h = canvas.height;
    const norm = normalizeSeries(labels, values, maxPoints);
    labels = norm.labels;
    values = norm.series;

    if (grid) {
      ctx.strokeStyle = "#eee";
      ctx.lineWidth = 1;
      for (let i = 0; i < 5; i++) {
        const y = (h / 5) * i;
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(w, y);
        ctx.stroke();
      }
    }

    if (!values || values.length === 0) return;
    const xs = values.map((_, i) => (w / Math.max(1, values.length - 1)) * i);
    const ys = scaleValues(values, h);

    // title
    if (title) {
      ctx.fillStyle = "#333";
      ctx.font = "14px sans-serif";
      ctx.fillText(title, 10, 16);
    }

    // yLabel
    if (yLabel) {
      ctx.save();
      ctx.translate(10, h / 2);
      ctx.rotate(-Math.PI / 2);
      ctx.fillStyle = "#333";
      ctx.font = "12px sans-serif";
      ctx.fillText(yLabel, 0, 0);
      ctx.restore();
    }

    ctx.beginPath();
    ctx.moveTo(xs[0], ys[0]);
    for (let i = 1; i < xs.length; i++) ctx.lineTo(xs[i], ys[i]);
    ctx.strokeStyle = strokeStyle;
    ctx.lineWidth = 2;
    ctx.stroke();
  }

  return {
    update({ labels, series }) {
      draw(labels, series);
    },
    destroy() {
      clearCanvas(canvas);
    },
  };
}

export function createBarChart(canvas, opts = {}) {
  const ctx = canvas.getContext("2d");
  const { fillStyle = "#2ecc71", title = "", yLabel = "", maxPoints = 500 } = opts;

  function draw(labels = [], values = []) {
    clearCanvas(canvas);
    const w = canvas.width;
    const h = canvas.height;
    const norm = normalizeSeries(labels, values, maxPoints);
    labels = norm.labels;
    values = norm.series;
    if (!values || values.length === 0) return;
    const barW = w / values.length;
    const max = Math.max(...values) || 1;
    // title
    if (title) {
      ctx.fillStyle = "#333";
      ctx.font = "14px sans-serif";
      ctx.fillText(title, 10, 16);
    }
    // yLabel
    if (yLabel) {
      ctx.save();
      ctx.translate(10, h / 2);
      ctx.rotate(-Math.PI / 2);
      ctx.fillStyle = "#333";
      ctx.font = "12px sans-serif";
      ctx.fillText(yLabel, 0, 0);
      ctx.restore();
    }
    values.forEach((v, i) => {
      const barH = (v / max) * (h - 40);
      ctx.fillStyle = fillStyle;
      ctx.fillRect(i * barW + 2, h - barH - 20, Math.max(1, barW - 4), barH);
    });
  }

  return {
    update({ labels, series }) {
      draw(labels, series);
    },
    destroy() {
      clearCanvas(canvas);
    },
  };
}

export function createAreaChart(canvas, opts = {}) {
  const ctx = canvas.getContext("2d");
  const { fillStyle = "rgba(52,152,219,0.3)", strokeStyle = "#2980b9", title = "", yLabel = "", maxPoints = 500 } = opts;

  function draw(labels = [], values = []) {
    clearCanvas(canvas);
    const w = canvas.width;
    const h = canvas.height;
    const norm = normalizeSeries(labels, values, maxPoints);
    labels = norm.labels;
    values = norm.series;
    if (!values || values.length === 0) return;
    const xs = values.map((_, i) => (w / Math.max(1, values.length - 1)) * i);
    const ys = scaleValues(values, h);

    if (title) {
      ctx.fillStyle = "#333";
      ctx.font = "14px sans-serif";
      ctx.fillText(title, 10, 16);
    }
    if (yLabel) {
      ctx.save();
      ctx.translate(10, h / 2);
      ctx.rotate(-Math.PI / 2);
      ctx.fillStyle = "#333";
      ctx.font = "12px sans-serif";
      ctx.fillText(yLabel, 0, 0);
      ctx.restore();
    }

    ctx.beginPath();
    ctx.moveTo(xs[0], ys[0]);
    for (let i = 1; i < xs.length; i++) ctx.lineTo(xs[i], ys[i]);
    ctx.lineTo(w, h - 20);
    ctx.lineTo(0, h - 20);
    ctx.closePath();
    ctx.fillStyle = fillStyle;
    ctx.fill();

    ctx.beginPath();
    ctx.moveTo(xs[0], ys[0]);
    for (let i = 1; i < xs.length; i++) ctx.lineTo(xs[i], ys[i]);
    ctx.strokeStyle = strokeStyle;
    ctx.lineWidth = 2;
    ctx.stroke();
  }

  return {
    update({ labels, series }) {
      draw(labels, series);
    },
    destroy() {
      clearCanvas(canvas);
    },
  };
}

export function createHeatmap(canvas, opts = {}) {
  const ctx = canvas.getContext("2d");

  function draw(grid = [[]], xLabels = [], yLabels = []) {
    clearCanvas(canvas);
    const w = canvas.width;
    const h = canvas.height;
    const rows = grid.length;
    const cols = grid[0] ? grid[0].length : 0;
    if (rows === 0 || cols === 0) return;
    const cellW = w / cols;
    const cellH = h / rows;
    let min = Infinity;
    let max = -Infinity;
    for (let r = 0; r < rows; r++)
      for (let c = 0; c < cols; c++) {
        const v = grid[r][c] ?? 0;
        if (v < min) min = v;
        if (v > max) max = v;
      }
    const range = max - min || 1;
    for (let r = 0; r < rows; r++) {
      for (let c = 0; c < cols; c++) {
        const v = grid[r][c] ?? 0;
        const t = (v - min) / range;
        const color = `rgb(${Math.round(255 * (1 - t))},${Math.round(255 * t)},150)`;
        ctx.fillStyle = color;
        ctx.fillRect(c * cellW, r * cellH, cellW, cellH);
      }
    }
  }

  return {
    update({ grid, xLabels, yLabels }) {
      draw(grid, xLabels, yLabels);
    },
    destroy() {
      clearCanvas(canvas);
    },
  };
}

export default {
  createLineChart,
  createBarChart,
  createAreaChart,
  createHeatmap,
};

/**
 * Temp tab logic
 */
import OpenMeteo from "../openMeteo.js";
import { createLineChart } from "../charts/simpleCharts.js";

/**
 * Load temperature metrics for the given city and render into the temp panel.
 */
export async function load(city) {
  const el = document.getElementById("temp-content");
  if (!city) {
    el.textContent = "Enter a city to see temperature data.";
    return null;
  }
  const canvas = document.getElementById("temp-canvas");
  el.hidden = true;
  canvas.hidden = false;
  const chart = createLineChart(canvas, { strokeStyle: "#e74c3c", title: `Temperature — ${city}`, yLabel: "°C" });
  canvas.getContext("2d").clearRect(0, 0, canvas.width, canvas.height);
  canvas.getContext("2d").font = "12px sans-serif";
  canvas.getContext("2d").fillText("Loading temperature...", 10, 20);

  const data = await OpenMeteo.getHourlyByType(city, "temp", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });
  if (!data || !data.hourly) {
    el.hidden = false;
    el.textContent = "No temperature data available.";
    canvas.hidden = true;
    return data;
  }

  const dates = data.hourly.time || [];
  const temps = data.hourly.temperature_2m || [];
  chart.update({ labels: dates, series: temps });
  return data;
}

export default { load };

/**
 * Rain tab logic
 */
import OpenMeteo from "../openMeteo.js";
import { createBarChart } from "../charts/simpleCharts.js";

/**
 * Load rainfall metrics for the given city and render into the rain panel.
 */
export async function load(city) {
  const el = document.getElementById("rain-content");
  if (!city) {
    el.textContent = "Enter a city to see rain data.";
    return null;
  }

  const canvas = document.getElementById("rain-canvas");
  el.hidden = true;
  canvas.hidden = false;
  const chart = createBarChart(canvas, { fillStyle: "#3498db", title: `Precipitation — ${city}`, yLabel: "mm" });

  const data = await OpenMeteo.getHourlyByType(city, "rain", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });
  if (!data || !data.hourly) {
    el.hidden = false;
    el.textContent = "No rain data available.";
    canvas.hidden = true;
    return data;
  }

  const dates = data.hourly.time || [];
  const precip = data.hourly.precipitation || [];
  chart.update({ labels: dates, series: precip });
  return data;
}

export default { load };

/**
 * Sun tab logic
 */
import OpenMeteo from "../openMeteo.js";
import { createAreaChart } from "../charts/simpleCharts.js";

/**
 * Load solar metrics (sunrise/sunset) for the given city and render into the sun panel.
 */
export async function load(city) {
  const el = document.getElementById("sun-content");
  if (!city) {
    el.textContent = "Enter a city to see sun data.";
    return null;
  }

  const canvas = document.getElementById("sun-canvas");
  el.hidden = true;
  canvas.hidden = false;
  const chart = createAreaChart(canvas, { title: `Shortwave Radiation — ${city}`, yLabel: "W/m²" });

  const [dailyData, hourlyData] = await Promise.all([
    OpenMeteo.getDailyForecast(city, { daily: ["sunrise", "sunset", "sunshine_duration"] }),
    OpenMeteo.getHourlyByType(city, "sun", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) }),
  ]);

  if (!dailyData || !dailyData.daily) {
    el.hidden = false;
    el.textContent = "No sun data available.";
    canvas.hidden = true;
    return dailyData;
  }

  const dates = dailyData.daily.time || [];
  const sunrise = dailyData.daily.sunrise || [];
  const sunset = dailyData.daily.sunset || [];

  const dailyLines = dates.map((d, i) => `${d}: sunrise ${sunrise[i]}  •  sunset ${sunset[i]}`);

  let times = [];
  let rad = [];
  if (hourlyData && hourlyData.hourly) {
    times = hourlyData.hourly.time || [];
    rad = hourlyData.hourly.shortwave_radiation || [];
  }

  chart.update({ labels: times, series: rad });
  el.hidden = false;
  el.textContent = dailyLines.join("\n");
  return { daily: dailyData.daily, hourly: hourlyData && hourlyData.hourly };
}

export default { load };

/**
 * Snow tab logic
 */
import OpenMeteo from "../openMeteo.js";
import { createBarChart } from "../charts/simpleCharts.js";

/**
 * Load snowfall metrics for the given city and render into the snow panel.
 */
export async function load(city) {
  const el = document.getElementById("snow-content");
  if (!city) {
    el.textContent = "Enter a city to see snow data.";
    return null;
  }

  const canvas = document.getElementById("snow-canvas");
  el.hidden = true;
  canvas.hidden = false;
  const chart = createBarChart(canvas, { fillStyle: "#95a5a6", title: `Snowfall — ${city}`, yLabel: "cm" });

  const data = await OpenMeteo.getHourlyByType(city, "snow", { startDate: new Date(), endDate: new Date(new Date().getTime() + 48 * 60 * 60 * 1000) });

  if (!data || !data.hourly) {
    el.hidden = false;
    el.textContent = "No snow data available.";
    canvas.hidden = true;
    return data;
  }

  const dates = data.hourly.time || [];
  const snow = data.hourly.snowfall || [];
  chart.update({ labels: dates, series: snow });
  return data;
}

export default { load };

