:root {
    /* Beispiel-Variablen - optional nutzen */
    --primary-color: #3498db;
    --secondary-color: #2c3e50;
    --background: #ecf0f1;
    --text-color: #333;
}

body {
    background-color: var(--background);
    color: var(--text-color);
}

#app {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
}

.search-form {
    display: flex;
    gap: 8px;
    align-items: center;
}

.search-form input[type="search"] {
    padding: 8px 12px;
    font-size: 1rem;
    border: 1px solid #ccc;
    border-radius: 4px;
    min-width: 260px;
}

.search-form button {
    padding: 8px 12px;
    background: var(--primary-color);
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

.tabs {
    margin-top: 20px;
}

.tab-list {
    display: flex;
    list-style: none;
    padding: 0;
    gap: 8px;
}

.tab-item {
    padding: 8px 12px;
    background: #fff;
    border-radius: 6px;
    cursor: pointer;
    border: 1px solid #ddd;
}

.tab-item.active {
    background: var(--primary-color);
    color: white;
}

.tab-panels {
    margin-top: 12px;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

.weather-output {
    margin-top: 16px;
}

#raw-output {
    white-space: pre-wrap;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

/* Forecast view */

.forecast-container {
    margin-top: 16px;
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 6px;
    padding: 12px;
    display: flex;
    gap: 12px;
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
}

/* Each day is a vertical column; days scroll horizontally */
.day-block {
    flex: 0 0 320px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: linear-gradient(180deg,#ffffff,#fbfcfe);
    border: 1px solid #eee;
    border-radius: 8px;
    padding: 10px;
    box-shadow: 0 1px 2px rgba(0,0,0,0.03);
}

.day-header {
    font-weight: 700;
    margin: 0;
    padding-bottom: 4px;
    border-bottom: 1px solid #f4f6f8;
}

/* The hours list is vertically scrollable inside each day column */
.hours {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 8px;
    overflow-y: auto;
    max-height: 60vh;
    padding-right: 6px; /* room for scrollbar */
}

.hour-row {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    border-radius: 6px;
    background: linear-gradient(180deg,#ffffff,#fcfdfe);
    border: 1px solid #f0f2f4;
    width: 100%;
}

.hour-time { width: 56px; font-size: 0.9rem; }
.hour-icon { width: 28px; text-align: center; font-size: 1.2rem; }
.hour-temp { width: 56px; font-weight: 600; }
.hour-precip { font-size: 0.85rem; color: #555; }

.no-data { color: #888; }

