:root {
    /* Beispiel-Variablen - optional nutzen */
    --primary-color: #3498db;
    --secondary-color: #2c3e50;
    --background: #ecf0f1;
    --text-color: #333;
}

body {
    background-color: var(--background);
    color: var(--text-color);
}

#app {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
}

.search-form {
    display: flex;
    gap: 8px;
    align-items: center;
}

.search-form input[type="search"] {
    padding: 8px 12px;
    font-size: 1rem;
    border: 1px solid #ccc;
    border-radius: 4px;
    min-width: 260px;
}

.search-form button {
    padding: 8px 12px;
    background: var(--primary-color);
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

.tabs {
    margin-top: 20px;
}

.tab-list {
    display: flex;
    list-style: none;
    padding: 0;
    gap: 8px;
}

.tab-item {
    padding: 8px 12px;
    background: #fff;
    border-radius: 6px;
    cursor: pointer;
    border: 1px solid #ddd;
}

.tab-item.active {
    background: var(--primary-color);
    color: white;
}

.tab-panels {
    margin-top: 12px;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

.weather-output {
    margin-top: 16px;
}

#raw-output {
    white-space: pre-wrap;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

/* Forecast view */

.forecast-container {
    margin-top: 16px;
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 6px;
    padding: 12px;
    display: block;
    gap: 12px;
}

/* Each day is a vertical column; days scroll horizontally */
.day-block {
    flex: 0 0 320px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: linear-gradient(180deg,#ffffff,#fbfcfe);
    border: 1px solid #eee;
    border-radius: 8px;
    padding: 10px;
    box-shadow: 0 1px 2px rgba(0,0,0,0.03);
}

.day-header {
    font-weight: 700;
    margin: 0;
    padding-bottom: 4px;
    border-bottom: 1px solid #f4f6f8;
}

/* The hours list is vertically scrollable inside each day column */
.hours {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 8px;
    overflow-y: auto;
    overflow-x: hidden;
    max-height: 60vh;
    padding-right: 6px; /* room for scrollbar */
}

.hour-row {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    border-radius: 6px;
    background: linear-gradient(180deg,#ffffff,#fcfdfe);
    border: 1px solid #f0f2f4;
    width: 100%;
    box-sizing: border-box;
}

/* Viewport and track for horizontal day scrolling */
.forecast-wrapper {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.forecast-viewport {
    overflow: hidden; /* hide native scrollbars */
    margin: 0 40px; /* space for nav buttons */
    box-sizing: border-box;
}

.days-track {
    display: flex;
    gap: 12px;
    transition: transform 320ms ease;
    will-change: transform;
}

.day-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid #ddd;
    background: #fff;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    box-shadow: 0 1px 2px rgba(0,0,0,0.06);
}

.day-nav.left { left: 6px; }
.day-nav.right { right: 6px; }

.day-nav[disabled] { opacity: 0.45; cursor: default; }

.hour-time { width: 56px; font-size: 0.9rem; }
.hour-icon { width: 28px; text-align: center; font-size: 1.2rem; }
.hour-temp { width: 56px; font-weight: 600; }
.hour-precip { font-size: 0.85rem; color: #555; }

.no-data { color: #888; }

import OpenMeteo from "./openMeteo.js";

const app = {
  async init() {
    this.cache = {};
    this.form = document.getElementById("search-form");
    this.input = document.getElementById("city-input");
    this.forecastContainer = document.getElementById("forecast-container");

    this.form.addEventListener("submit", async (ev) => {
      ev.preventDefault();
      const city = this.input.value.trim();
      if (!city) return;
      await this.handleSearch(city);
    });
  },

  async handleSearch(city) {
    try {
      this.setRawOutput("Loading...");
      if (this.forecastContainer) this.forecastContainer.innerHTML = "<div class=\"no-data\">Loading forecast...</div>";

      const currentResp = await OpenMeteo.getCurrentWeather(city);

      // Request a few days of hourly variables we need
      const endDate = new Date(Date.now() + 3 * 24 * 60 * 60 * 1000);
      const hourlyResp = await OpenMeteo.getHourlyByVariables(city, [
        "temperature_2m",
        "precipitation",
        "rain",
        "showers",
        "snowfall",
        "weathercode",
        "shortwave_radiation",
        "is_day",
      ], { startDate: new Date(), endDate });

      const combined = {
        city,
        current: currentResp.current,
        location: currentResp.location,
        hourly: hourlyResp.hourly,
      };

      this.cache[city.toLowerCase()] = combined;
      this.renderForecast(combined);
      this.setRawOutput(JSON.stringify({ location: combined.location }, null, 2));
    } catch (err) {
      this.setRawOutput(`Error: ${err.message}`);
      console.error(err);
      if (this.forecastContainer) this.forecastContainer.innerHTML = `<div class="no-data">Error loading forecast: ${err.message}</div>`;
    }
  },

  renderForecast(data) {
    const container = this.forecastContainer;
    if (!container) return;
    const hourly = data.hourly;
    if (!hourly || !hourly.time) {
      container.innerHTML = '<div class="no-data">No hourly data available.</div>';
      return;
    }

    const times = hourly.time || [];
    const temps = hourly.temperature_2m || [];
    const precip = hourly.precipitation || [];
    const snowfall = hourly.snowfall || [];
    const rain = hourly.rain || [];
    const showers = hourly.showers || [];
    const weathercode = hourly.weathercode || [];
    const shortwave = hourly.shortwave_radiation || [];
    const is_day = hourly.is_day || [];

    // Group by local day (YYYY-MM-DD)
    const days = {};
    for (let i = 0; i < times.length; i++) {
      const t = times[i];
      const d = new Date(t);
      const dayKey = d.toISOString().slice(0, 10);
      if (!days[dayKey]) days[dayKey] = [];
      days[dayKey].push({
        time: d,
        temp: temps[i],
        precip: precip[i],
        snowfall: snowfall[i],
        rain: rain[i],
        showers: showers[i],
        weathercode: weathercode[i],
        shortwave: shortwave[i],
        is_day: is_day[i],
      });
    }

    let html = "";
    const dayKeys = Object.keys(days).sort();
    // Build day columns inside a days-track so we can translate it horizontally
    html += `<div class="forecast-wrapper"><button class="day-nav left" aria-label="Previous day">◀</button><div class="forecast-viewport"><div class="days-track">`;
    for (const dayKey of dayKeys) {
      const entries = days[dayKey];
      const totalPrecip = entries.reduce((s, e) => s + (Number(e.precip) || 0), 0).toFixed(1);
      const tempsArr = entries.map((e) => (Number(e.temp) || -999)).filter((v) => v > -998);
      const maxTemp = tempsArr.length ? Math.max(...tempsArr) : null;
      const dateObj = new Date(dayKey + 'T00:00:00');
      const headerLabel = dateObj.toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' });

      html += `<div class="day-block"><div class="day-header">${headerLabel} — ${maxTemp !== null ? `max ${maxTemp}°C` : ''} • total ${totalPrecip} mm</div><div class="hours" role="list">`;

      for (const e of entries) {
        const hh = e.time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        const icon = getIconForEntry(e);
        const precipLabel = e.precip ? `${e.precip} mm` : '';
        const snowLabel = e.snowfall ? `${e.snowfall} cm` : '';
        html += `<div class="hour-row" role="listitem" data-time="${e.time.toISOString()}"><div class="hour-time">${hh}</div><div class="hour-icon">${icon}</div><div class="hour-temp">${e.temp ?? '--'}°</div><div class="hour-precip">${precipLabel}${snowLabel ? ' / ' + snowLabel : ''}</div></div>`;
      }

      html += `</div></div>`;
    }
    html += `</div></div><button class="day-nav right" aria-label="Next day">▶</button></div>`;

    container.innerHTML = html;

    // After DOM insert, wire up navigation to translate the days-track by one day width.
    const viewport = container.querySelector('.forecast-viewport');
    const track = container.querySelector('.days-track');
    const leftBtn = container.querySelector('.day-nav.left');
    const rightBtn = container.querySelector('.day-nav.right');

    if (!track || !viewport) return;

    const dayGap = 12; // must match CSS gap
    const dayNode = track.querySelector('.day-block');
    const dayWidth = dayNode ? dayNode.offsetWidth : 320;
    const visible = Math.min(5, dayKeys.length);
    // Set viewport width to show exactly `visible` days (including gaps between them)
    viewport.style.width = `${visible * dayWidth + (visible - 1) * dayGap}px`;

    let index = 0; // first visible day index

    function updateNavState() {
      leftBtn.toggleAttribute('disabled', index === 0);
      rightBtn.toggleAttribute('disabled', index >= Math.max(0, dayKeys.length - visible));
      const translateX = -(index * (dayWidth + dayGap));
      track.style.transform = `translateX(${translateX}px)`;
    }

    leftBtn.addEventListener('click', () => {
      index = Math.max(0, index - 1);
      updateNavState();
    });
    rightBtn.addEventListener('click', () => {
      index = Math.min(Math.max(0, dayKeys.length - visible), index + 1);
      updateNavState();
    });

    // initial state
    updateNavState();
  },

  setRawOutput(text) {
    const raw = document.getElementById("raw-output");
    if (raw) raw.textContent = text;
  },
};

function getIconForEntry(entry) {
  // Snow takes precedence
  const snow = Number(entry.snowfall) || 0;
  const p = Number(entry.precip) || 0;
  const sw = Number(entry.shortwave) || 0;
  const day = entry.is_day === 1 || entry.is_day === true;

  if (snow > 0.1) return '❄️';
  if (p > 10) return '💦';
  if (p > 2) return '💧💧';
  if (p > 0.1) return '💧';
  if (day && sw > 120) return '☀️';
  // fallback by weathercode: 0 clear
  if (entry.weathercode === 0) return '☀️';
  return '⛅';
}

// Start app when DOM is loaded
document.addEventListener("DOMContentLoaded", () => {
  app.init();
});

