:root {
    /* Beispiel-Variablen - optional nutzen */
    --primary-color: #3498db;
    --secondary-color: #2c3e50;
    --background: #ecf0f1;
    --text-color: #333;
}

body {
    background-color: var(--background);
    color: var(--text-color);
}

#app {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
}

.search-form {
    display: flex;
    gap: 8px;
    align-items: center;
}

.search-form input[type="search"] {
    padding: 8px 12px;
    font-size: 1rem;
    border: 1px solid #ccc;
    border-radius: 4px;
    min-width: 260px;
}

.search-form button {
    padding: 8px 12px;
    background: var(--primary-color);
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

.tabs {
    margin-top: 20px;
}

.tab-list {
    display: flex;
    list-style: none;
    padding: 0;
    gap: 8px;
}

.tab-item {
    padding: 8px 12px;
    background: #fff;
    border-radius: 6px;
    cursor: pointer;
    border: 1px solid #ddd;
}

.tab-item.active {
    background: var(--primary-color);
    color: white;
}

.tab-panels {
    margin-top: 12px;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

.weather-output {
    margin-top: 16px;
}

#raw-output {
    white-space: pre-wrap;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

/* Forecast view */

.forecast-container {
    margin-top: 16px;
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 6px;
    padding: 12px;
    display: block;
    gap: 12px;
}

/* Each day is a vertical column; days scroll horizontally */
.day-block {
    flex: 0 0 320px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: linear-gradient(180deg,#ffffff,#fbfcfe);
    border: 1px solid #eee;
    border-radius: 8px;
    padding: 10px;
    box-shadow: 0 1px 2px rgba(0,0,0,0.03);
}

.day-header {
    font-weight: 700;
    margin: 0;
    padding-bottom: 4px;
    border-bottom: 1px solid #f4f6f8;
}

/* The hours list is vertically scrollable inside each day column */
.hours {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 8px;
    overflow-y: auto;
    overflow-x: hidden;
    max-height: 60vh;
    padding-right: 6px; /* room for scrollbar */
}

.hour-row {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    border-radius: 6px;
    background: linear-gradient(180deg,#ffffff,#fcfdfe);
    border: 1px solid #f0f2f4;
    width: 100%;
    box-sizing: border-box;
}

/* Time-of-day indicator */
.time-indicator {
    width: 10px;
    height: 28px;
    border-radius: 6px;
    flex: 0 0 10px;
    margin-right: 6px;
    box-shadow: inset 0 0 0 1px rgba(255,255,255,0.02);
}
.time-indicator.morning { background: linear-gradient(180deg,#fff3c4,#ffd97a); }
.time-indicator.day { background: linear-gradient(180deg,#fff39c,#ffd24d); }
.time-indicator.evening { background: linear-gradient(180deg,#ffd2a6,#ff9f6b); }
.time-indicator.night { background: linear-gradient(180deg,#2b3a67,#18203a); }

.hour-time { width: 56px; font-size: 0.9rem; }

/* Viewport and track for horizontal day scrolling */
.forecast-wrapper {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.forecast-viewport {
    overflow: hidden; /* hide native scrollbars */
    margin: 0 40px; /* space for nav buttons */
    box-sizing: border-box;
}

.days-track {
    display: flex;
    gap: 12px;
    transition: transform 320ms ease;
    will-change: transform;
}

.day-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid #ddd;
    background: #fff;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    box-shadow: 0 1px 2px rgba(0,0,0,0.06);
}

.day-nav.left { left: 6px; }
.day-nav.right { right: 6px; }

.day-nav[disabled] { opacity: 0.45; cursor: default; }

/* Simple clickable day tabs used to select a single day's hours */
.day-tabs {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 6px;
    margin-bottom: 8px;
}

.day-tab {
    padding: 6px 10px;
    background: #fff;
    border: 1px solid #e6eef6;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 600;
}

.day-tab.selected {
    background: var(--primary-color);
    color: white;
    border-color: var(--primary-color);
}

.single-day {
    display: block;
}

.hour-time { width: 56px; font-size: 0.9rem; }
.hour-icon { width: 28px; text-align: center; font-size: 1.2rem; }
.hour-temp { width: 56px; font-weight: 600; }
.hour-precip { font-size: 0.85rem; color: #555; }

.no-data { color: #888; }

