:root {
    --primary-color: #0f62fe; /* vivid blue */
    --primary-600: #095bd8;
    --muted: #6b7684;
    --bg-1: #f6f9fc;
    --card: #ffffff;
    --glass: rgba(255,255,255,0.6);
    --text: #0f1724;
    --radius: 12px;
}

body {
    font-family: Inter, ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial;
    background: linear-gradient(180deg, #f3f7fb 0%, #eef6ff 100%);
    color: var(--text);
    -webkit-font-smoothing:antialiased;
    -moz-osx-font-smoothing:grayscale;
}

#app {
    max-width: 1100px;
    margin: 28px auto;
    padding: 20px;
}

header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
    margin-bottom: 8px;
}

.search-form {
    display: flex;
    gap: 8px;
    align-items: center;
}

.search-form input[type="search"] {
    padding: 10px 14px;
    font-size: 1rem;
    border: 1px solid rgba(15,23,36,0.06);
    border-radius: 10px;
    min-width: 320px;
    background: white;
    box-shadow: 0 6px 18px rgba(15,23,36,0.04);
    transition: box-shadow 180ms ease, transform 120ms ease;
}

.search-form input[type="search"]:focus {
    outline: none;
    box-shadow: 0 10px 30px rgba(15,62,255,0.12);
    transform: translateY(-1px);
}

.search-form button {
    padding: 10px 14px;
    background: linear-gradient(180deg,var(--primary-color),var(--primary-600));
    color: white;
    border: none;
    border-radius: 10px;
    cursor: pointer;
    box-shadow: 0 6px 18px rgba(15,62,255,0.12);
    transition: transform 120ms ease, box-shadow 120ms ease;
}

.search-form button:active {
    transform: translateY(1px) scale(0.995);
    box-shadow: 0 3px 8px rgba(15,62,255,0.08) inset;
}

.tabs {
    margin-top: 20px;
}

.tab-list {
    display: flex;
    list-style: none;
    padding: 0;
    gap: 8px;
}

.tab-item {
    padding: 8px 12px;
    background: var(--card);
    border-radius: 8px;
    cursor: pointer;
    border: 1px solid rgba(15,23,36,0.06);
    transition: transform 140ms ease, box-shadow 140ms ease;
}

.tab-item:hover { transform: translateY(-3px); box-shadow: 0 8px 24px rgba(15,23,36,0.06); }

.tab-item.active {
    background: linear-gradient(180deg,var(--primary-color),var(--primary-600));
    color: white;
    box-shadow: 0 10px 30px rgba(15,62,255,0.12);
}

.tab-panels {
    margin-top: 12px;
    background: var(--card);
    padding: 14px;
    border-radius: var(--radius);
    border: 1px solid rgba(15,23,36,0.04);
    box-shadow: 0 8px 30px rgba(15,23,36,0.04);
}

.weather-output {
    margin-top: 16px;
}

#raw-output {
    white-space: pre-wrap;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

/* Forecast view */

.forecast-container {
    margin-top: 18px;
    background: transparent;
    padding: 6px;
}

/* Each day is a vertical column; days scroll horizontally */
.day-block {
    flex: 0 0 340px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: linear-gradient(180deg,#ffffff,#fbfcfe);
    border: 1px solid rgba(15,23,36,0.04);
    border-radius: 12px;
    padding: 12px;
    box-shadow: 0 8px 24px rgba(15,23,36,0.06);
}

.day-header {
    font-weight: 700;
    margin: 0;
    padding-bottom: 6px;
    border-bottom: 1px solid rgba(15,23,36,0.03);
    color: #10243a;
    font-size: 0.95rem;
}

/* The hours list is vertically scrollable inside each day column */
.hours {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-top: 10px;
    overflow-y: auto;
    overflow-x: hidden;
    max-height: 60vh;
    padding-right: 6px; /* room for scrollbar */
}

.hour-row {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 10px 12px;
    border-radius: 10px;
    background: linear-gradient(180deg,#ffffff,#fbfdff);
    border: 1px solid rgba(15,23,36,0.04);
    width: 100%;
    box-sizing: border-box;
    transition: transform 140ms ease, box-shadow 140ms ease, background 160ms ease;
}

.hour-row:hover { transform: translateY(-3px); box-shadow: 0 10px 30px rgba(15,23,36,0.06); }

.hour-row:active { transform: translateY(0px) scale(0.997); box-shadow: 0 6px 16px rgba(15,23,36,0.04) inset; }

/* Time-of-day indicator */
.time-indicator {
    width: 12px;
    height: 32px;
    border-radius: 8px;
    flex: 0 0 12px;
    margin-right: 8px;
    box-shadow: inset 0 0 0 1px rgba(255,255,255,0.02);
}
.time-indicator.morning { background: linear-gradient(180deg,#fff6e6,#ffd98a); }
.time-indicator.day { background: linear-gradient(180deg,#fff5d6,#ffd24d); }
.time-indicator.evening { background: linear-gradient(180deg,#ffe3d2,#ffb07a); }
.time-indicator.night { background: linear-gradient(180deg,#233659,#122032); }

.hour-time { width: 56px; font-size: 0.95rem; color: var(--muted); }

/* Viewport and track for horizontal day scrolling */
.forecast-wrapper {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.forecast-viewport {
    overflow: hidden; /* hide native scrollbars */
    margin: 0 40px; /* space for nav buttons */
    box-sizing: border-box;
}

.days-track {
    display: flex;
    gap: 12px;
    transition: transform 320ms ease;
    will-change: transform;
}

.day-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid #ddd;
    background: #fff;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    box-shadow: 0 1px 2px rgba(0,0,0,0.06);
}

.day-nav.left { left: 6px; }
.day-nav.right { right: 6px; }

.day-nav[disabled] { opacity: 0.45; cursor: default; }

/* Simple clickable day tabs used to select a single day's hours */
.day-tabs {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 6px;
    margin-bottom: 8px;
}

.day-tab {
    padding: 6px 10px;
    background: #fff;
    border: 1px solid #e6eef6;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 600;
}

.day-tab.selected {
    background: var(--primary-color);
    color: white;
    border-color: var(--primary-color);
}

.single-day {
    display: block;
}

.hour-time { width: 56px; font-size: 0.9rem; }
.hour-icon { width: 28px; text-align: center; font-size: 1.2rem; }
.hour-temp { width: 56px; font-weight: 600; }
.hour-precip { font-size: 0.85rem; color: #555; }

.no-data { color: #888; }

