import OpenMeteo from "./openMeteo.js";

const app = {
  async init() {
    this.cache = {};
    this.form = document.getElementById("search-form");
    this.input = document.getElementById("city-input");
    this.forecastContainer = document.getElementById("forecast-container");

    this.form.addEventListener("submit", async (ev) => {
      ev.preventDefault();
      const city = this.input.value.trim();
      if (!city) return;
      await this.handleSearch(city);
    });
  },

  async handleSearch(city) {
    try {
      if (this.forecastContainer) this.forecastContainer.innerHTML = "<div class=\"no-data\">Loading forecast...</div>";

      const currentResp = await OpenMeteo.getCurrentWeather(city);

      // Request a few days of hourly variables we need
      const endDate = new Date(Date.now() + 3 * 24 * 60 * 60 * 1000);
      const hourlyResp = await OpenMeteo.getHourlyByVariables(city, [
        "temperature_2m",
        "precipitation",
        "rain",
        "showers",
        "snowfall",
        "weathercode",
        "shortwave_radiation",
        "is_day",
      ], { startDate: new Date(), endDate });

      const combined = {
        city,
        current: currentResp.current,
        location: currentResp.location,
        hourly: hourlyResp.hourly,
      };

      this.cache[city.toLowerCase()] = combined;
      this.renderForecast(combined);
    } catch (err) {
      console.error(err);
      if (this.forecastContainer) this.forecastContainer.innerHTML = `<div class="no-data">Error loading forecast: ${err.message}</div>`;
    }
  },

  renderForecast(data) {
    const container = this.forecastContainer;
    if (!container) return;
    const hourly = data.hourly;
    if (!hourly || !hourly.time) {
      container.innerHTML = '<div class="no-data">No hourly data available.</div>';
      return;
    }

    const times = hourly.time || [];
    const temps = hourly.temperature_2m || [];
    const precip = hourly.precipitation || [];
    const snowfall = hourly.snowfall || [];
    const rain = hourly.rain || [];
    const showers = hourly.showers || [];
    const weathercode = hourly.weathercode || [];
    const shortwave = hourly.shortwave_radiation || [];
    const is_day = hourly.is_day || [];

    // Group by local day (YYYY-MM-DD)
    const days = {};
    for (let i = 0; i < times.length; i++) {
      const t = times[i];
      const d = new Date(t);
      const dayKey = d.toISOString().slice(0, 10);
      if (!days[dayKey]) days[dayKey] = [];
      days[dayKey].push({
        time: d,
        temp: temps[i],
        precip: precip[i],
        snowfall: snowfall[i],
        rain: rain[i],
        showers: showers[i],
        weathercode: weathercode[i],
        shortwave: shortwave[i],
        is_day: is_day[i],
      });
    }

    let html = "";
    const dayKeys = Object.keys(days).sort();

    // Build a simple day tab list and a single-day column area
    html += `<div class="day-tabs" role="tablist">`;
    for (let i = 0; i < dayKeys.length; i++) {
      const dayKey = dayKeys[i];
      const dateObj = new Date(dayKey + 'T00:00:00');
      const headerLabel = dateObj.toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' });
      const sel = i === 0 ? ' selected' : '';
      html += `<button class="day-tab${sel}" data-day="${dayKey}" role="tab" aria-selected="${i === 0}">${headerLabel}</button>`;
    }
    html += `</div>`;

    // Render the first day column initially
    const firstKey = dayKeys[0];
    html += `<div class="single-day">`;
    html += renderDayHtml(firstKey, days[firstKey]);
    html += `</div>`;

    container.innerHTML = html;

    // Helper to render day's inner HTML for reuse
    function renderDayHtml(dayKey, entries) {
      const totalPrecip = entries.reduce((s, e) => s + (Number(e.precip) || 0), 0).toFixed(1);
      const tempsArr = entries.map((e) => (Number(e.temp) || -999)).filter((v) => v > -998);
      const maxTemp = tempsArr.length ? Math.max(...tempsArr) : null;
      const dateObj = new Date(dayKey + 'T00:00:00');
      const headerLabel = dateObj.toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' });
      let inner = `<div class="day-header">${headerLabel} — ${maxTemp !== null ? `max ${maxTemp}°C` : ''} • total ${totalPrecip} mm</div><div class="hours" role="list">`;
      for (const e of entries) {
        const hh = e.time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        const icon = getIconForEntry(e);
        const precipLabel = e.precip ? `${e.precip} mm` : '';
        const snowLabel = e.snowfall ? `${e.snowfall} cm` : '';
        // determine time-of-day class
        const hr = e.time.getHours();
        let tod = 'day';
        if (!e.is_day || e.is_day === 0) tod = 'night';
        else if (hr >= 5 && hr < 11) tod = 'morning';
        else if (hr >= 11 && hr < 17) tod = 'day';
        else tod = 'evening';

        const todLabel = tod[0].toUpperCase() + tod.slice(1);
        const weatherText = weatherCodeToText(Number(e.weathercode));

        // precipitation icons
        const precipIcon = (Number(e.precip) || 0) > 0 ? `<span class="precip-icon" title="Precipitation: ${precipLabel}">💧</span>` : '';
        const snowIcon = (Number(e.snowfall) || 0) > 0 ? `<span class="snow-icon" title="Snowfall: ${snowLabel}">❄️</span>` : '';

        inner += `<div class="hour-row" role="listitem" data-time="${e.time.toISOString()}"><div class="time-indicator ${tod}" title="${todLabel}" aria-hidden="true"></div><div class="hour-time">${hh}</div><div class="hour-icon" title="${weatherText}">${icon}</div><div class="hour-temp" title="Temperature">${e.temp ?? '--'}°</div><div class="hour-precip">${precipIcon}${precipLabel}${snowIcon ? ' ' + snowIcon + snowLabel : ''}</div></div>`;
      }
      inner += `</div>`;
      return inner;
    }

    // Wire up tab clicks to swap the single-day view
    const tabs = container.querySelectorAll('.day-tab');
    const singleDay = container.querySelector('.single-day');
    tabs.forEach((btn) => {
      btn.addEventListener('click', (ev) => {
        const target = ev.currentTarget;
        const dayKey = target.getAttribute('data-day');
        // update selection classes and aria
        tabs.forEach((t) => { t.classList.remove('selected'); t.setAttribute('aria-selected', 'false'); });
        target.classList.add('selected');
        target.setAttribute('aria-selected', 'true');
        // update the day column
        singleDay.innerHTML = renderDayHtml(dayKey, days[dayKey]);
      });
    });
  },

  setRawOutput(text) {
    const raw = document.getElementById("raw-output");
    if (raw) raw.textContent = text;
  },
};

function getIconForEntry(entry) {
  // Snow takes precedence
  const snow = Number(entry.snowfall) || 0;
  const p = Number(entry.precip) || 0;
  const sw = Number(entry.shortwave) || 0;
  const day = entry.is_day === 1 || entry.is_day === true;

  if (snow > 0.1) return '❄️';
  if (p > 10) return '💦';
  if (p > 2) return '💧💧';
  if (p > 0.1) return '💧';
  if (day && sw > 120) return '☀️';
  // fallback by weathercode: 0 clear — respect day/night
  if (entry.weathercode === 0) return day ? '☀️' : '🌙';
  // default: show partly-cloudy in day, cloud at night
  return day ? '⛅' : '☁️';
}

// Start app when DOM is loaded
document.addEventListener("DOMContentLoaded", () => {
  app.init();
});

import OpenMeteo from "./openMeteo.js";

const app = {
  async init() {
    this.cache = {};
    this.form = document.getElementById("search-form");
    this.input = document.getElementById("city-input");
    this.forecastContainer = document.getElementById("forecast-container");

    this.form.addEventListener("submit", async (ev) => {
      ev.preventDefault();
      const city = this.input.value.trim();
      if (!city) return;
      await this.handleSearch(city);
    });
  },

  async handleSearch(city) {
    try {
      if (this.forecastContainer) this.forecastContainer.innerHTML = "<div class=\"no-data\">Loading forecast...</div>";

      const currentResp = await OpenMeteo.getCurrentWeather(city);

      // Request a few days of hourly variables we need
      const endDate = new Date(Date.now() + 3 * 24 * 60 * 60 * 1000);
      const hourlyResp = await OpenMeteo.getHourlyByVariables(city, [
        "temperature_2m",
        "precipitation",
        "rain",
        "showers",
        "snowfall",
        "weathercode",
        "shortwave_radiation",
        "is_day",
      ], { startDate: new Date(), endDate });

      const combined = {
        city,
        current: currentResp.current,
        location: currentResp.location,
        hourly: hourlyResp.hourly,
      };

      this.cache[city.toLowerCase()] = combined;
      this.renderForecast(combined);
    } catch (err) {
      console.error(err);
      if (this.forecastContainer) this.forecastContainer.innerHTML = `<div class="no-data">Error loading forecast: ${err.message}</div>`;
    }
  },

  renderForecast(data) {
    const container = this.forecastContainer;
    if (!container) return;
    const hourly = data.hourly;
    if (!hourly || !hourly.time) {
      container.innerHTML = '<div class="no-data">No hourly data available.</div>';
      return;
    }

    const times = hourly.time || [];
    const temps = hourly.temperature_2m || [];
    const precip = hourly.precipitation || [];
    const snowfall = hourly.snowfall || [];
    const rain = hourly.rain || [];
    const showers = hourly.showers || [];
    const weathercode = hourly.weathercode || [];
    const shortwave = hourly.shortwave_radiation || [];
    const is_day = hourly.is_day || [];

    // Group by local day (YYYY-MM-DD)
    const days = {};
    for (let i = 0; i < times.length; i++) {
      const t = times[i];
      const d = new Date(t);
      const dayKey = d.toISOString().slice(0, 10);
      if (!days[dayKey]) days[dayKey] = [];
      days[dayKey].push({
        time: d,
        temp: temps[i],
        precip: precip[i],
        snowfall: snowfall[i],
        rain: rain[i],
        showers: showers[i],
        weathercode: weathercode[i],
        shortwave: shortwave[i],
        is_day: is_day[i],
      });
    }

    let html = "";
    const dayKeys = Object.keys(days).sort();

    // Build a simple day tab list and a single-day column area
    html += `<div class="day-tabs" role="tablist">`;
    for (let i = 0; i < dayKeys.length; i++) {
      const dayKey = dayKeys[i];
      const dateObj = new Date(dayKey + 'T00:00:00');
      const headerLabel = dateObj.toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' });
      const sel = i === 0 ? ' selected' : '';
      html += `<button class="day-tab${sel}" data-day="${dayKey}" role="tab" aria-selected="${i === 0}">${headerLabel}</button>`;
    }
    html += `</div>`;

    // Render the first day column initially
    const firstKey = dayKeys[0];
    html += `<div class="single-day">`;
    html += renderDayHtml(firstKey, days[firstKey]);
    html += `</div>`;

    container.innerHTML = html;

    // Helper to render day's inner HTML for reuse
    function renderDayHtml(dayKey, entries) {
      const totalPrecip = entries.reduce((s, e) => s + (Number(e.precip) || 0), 0).toFixed(1);
      const tempsArr = entries.map((e) => (Number(e.temp) || -999)).filter((v) => v > -998);
      const maxTemp = tempsArr.length ? Math.max(...tempsArr) : null;
      const dateObj = new Date(dayKey + 'T00:00:00');
      const headerLabel = dateObj.toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' });
      let inner = `<div class="day-header">${headerLabel} — ${maxTemp !== null ? `max ${maxTemp}°C` : ''} • total ${totalPrecip} mm</div><div class="hours" role="list">`;
      for (const e of entries) {
        const hh = e.time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        const icon = getIconForEntry(e);
        const precipLabel = e.precip ? `${e.precip} mm` : '';
        const snowLabel = e.snowfall ? `${e.snowfall} cm` : '';
        // determine time-of-day class
        const hr = e.time.getHours();
        let tod = 'day';
        if (!e.is_day || e.is_day === 0) tod = 'night';
        else if (hr >= 5 && hr < 11) tod = 'morning';
        else if (hr >= 11 && hr < 17) tod = 'day';
        else tod = 'evening';

        const todLabel = tod[0].toUpperCase() + tod.slice(1);
        const weatherText = weatherCodeToText(Number(e.weathercode));

        // precipitation icons
        const precipIcon = (Number(e.precip) || 0) > 0 ? `<span class="precip-icon" title="Precipitation: ${precipLabel}">💧</span>` : '';
        const snowIcon = (Number(e.snowfall) || 0) > 0 ? `<span class="snow-icon" title="Snowfall: ${snowLabel}">❄️</span>` : '';

        inner += `<div class="hour-row" role="listitem" data-time="${e.time.toISOString()}"><div class="time-indicator ${tod}" title="${todLabel}" aria-hidden="true"></div><div class="hour-time">${hh}</div><div class="hour-icon" title="${weatherText}">${icon}</div><div class="hour-temp" title="Temperature">${e.temp ?? '--'}°</div><div class="hour-precip">${precipIcon}${precipLabel}${snowIcon ? ' ' + snowIcon + snowLabel : ''}</div></div>`;
      }
      inner += `</div>`;
      return inner;
    }

    // Wire up tab clicks to swap the single-day view
    const tabs = container.querySelectorAll('.day-tab');
    const singleDay = container.querySelector('.single-day');
    tabs.forEach((btn) => {
      btn.addEventListener('click', (ev) => {
        const target = ev.currentTarget;
        const dayKey = target.getAttribute('data-day');
        // update selection classes and aria
        tabs.forEach((t) => { t.classList.remove('selected'); t.setAttribute('aria-selected', 'false'); });
        target.classList.add('selected');
        target.setAttribute('aria-selected', 'true');
        // update the day column
        singleDay.innerHTML = renderDayHtml(dayKey, days[dayKey]);
      });
    });
  },

  setRawOutput(text) {
    const raw = document.getElementById("raw-output");
    if (raw) raw.textContent = text;
  },
};

function getIconForEntry(entry) {
  // Snow takes precedence
  const snow = Number(entry.snowfall) || 0;
  const p = Number(entry.precip) || 0;
  const sw = Number(entry.shortwave) || 0;
  const day = entry.is_day === 1 || entry.is_day === true;

  if (snow > 0.1) return '❄️';
  if (p > 10) return '💦';
  if (p > 2) return '💧💧';
  if (p > 0.1) return '💧';
  if (day && sw > 120) return '☀️';
  // fallback by weathercode: 0 clear — respect day/night
  if (entry.weathercode === 0) return day ? '☀️' : '🌙';
  // default: show partly-cloudy in day, cloud at night
  return day ? '⛅' : '☁️';
}

/**
 * Map WMO `weathercode` to human readable text.
 */
function weatherCodeToText(code) {
  const map = {
    0: 'Clear sky',
    1: 'Mainly clear',
    2: 'Partly cloudy',
    3: 'Overcast',
    45: 'Fog',
    48: 'Depositing rime fog',
    51: 'Light drizzle',
    53: 'Moderate drizzle',
    55: 'Dense drizzle',
    56: 'Light freezing drizzle',
    57: 'Dense freezing drizzle',
    61: 'Slight rain',
    63: 'Moderate rain',
    65: 'Heavy rain',
    66: 'Light freezing rain',
    67: 'Heavy freezing rain',
    71: 'Slight snow fall',
    73: 'Moderate snow fall',
    75: 'Heavy snow fall',
    80: 'Rain showers: Slight',
    81: 'Rain showers: Moderate',
    82: 'Rain showers: Violent',
    85: 'Snow showers: Slight',
    86: 'Snow showers: Heavy',
    95: 'Thunderstorm',
    96: 'Thunderstorm with slight hail',
    99: 'Thunderstorm with heavy hail',
  };
  return map[code] || `Weather code ${code}`;
}

// Start app when DOM is loaded
document.addEventListener("DOMContentLoaded", () => {
  app.init();
});

:root {
    --primary-color: #0f62fe; /* vivid blue */
    --primary-600: #095bd8;
    --muted: #6b7684;
    --bg-1: #f6f9fc;
    --card: #ffffff;
    --glass: rgba(255,255,255,0.6);
    --text: #0f1724;
    --radius: 12px;
}

body {
    font-family: Inter, ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial;
    background: linear-gradient(180deg, #f3f7fb 0%, #eef6ff 100%);
    color: var(--text);
    -webkit-font-smoothing:antialiased;
    -moz-osx-font-smoothing:grayscale;
}

#app {
    max-width: 1100px;
    margin: 28px auto;
    padding: 20px;
}

header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
    margin-bottom: 8px;
}

.search-form {
    display: flex;
    gap: 8px;
    align-items: center;
}

.search-form input[type="search"] {
    padding: 10px 14px;
    font-size: 1rem;
    border: 1px solid rgba(15,23,36,0.06);
    border-radius: 10px;
    min-width: 320px;
    background: white;
    box-shadow: 0 6px 18px rgba(15,23,36,0.04);
    transition: box-shadow 180ms ease, transform 120ms ease;
}

.search-form input[type="search"]:focus {
    outline: none;
    box-shadow: 0 10px 30px rgba(15,62,255,0.12);
    transform: translateY(-1px);
}

.search-form button {
    padding: 10px 14px;
    background: linear-gradient(180deg,var(--primary-color),var(--primary-600));
    color: white;
    border: none;
    border-radius: 10px;
    cursor: pointer;
    box-shadow: 0 6px 18px rgba(15,62,255,0.12);
    transition: transform 120ms ease, box-shadow 120ms ease;
}

.search-form button:active {
    transform: translateY(1px) scale(0.995);
    box-shadow: 0 3px 8px rgba(15,62,255,0.08) inset;
}

.tabs {
    margin-top: 20px;
}

.tab-list {
    display: flex;
    list-style: none;
    padding: 0;
    gap: 8px;
}

.tab-item {
    padding: 8px 12px;
    background: var(--card);
    border-radius: 8px;
    cursor: pointer;
    border: 1px solid rgba(15,23,36,0.06);
    transition: transform 140ms ease, box-shadow 140ms ease;
}

.tab-item:hover { transform: translateY(-3px); box-shadow: 0 8px 24px rgba(15,23,36,0.06); }

.tab-item.active {
    background: linear-gradient(180deg,var(--primary-color),var(--primary-600));
    color: white;
    box-shadow: 0 10px 30px rgba(15,62,255,0.12);
}

.tab-panels {
    margin-top: 12px;
    background: var(--card);
    padding: 14px;
    border-radius: var(--radius);
    border: 1px solid rgba(15,23,36,0.04);
    box-shadow: 0 8px 30px rgba(15,23,36,0.04);
}

.weather-output {
    margin-top: 16px;
}

#raw-output {
    white-space: pre-wrap;
    background: #fff;
    padding: 12px;
    border-radius: 6px;
    border: 1px solid #ddd;
}

/* Forecast view */

.forecast-container {
    margin-top: 18px;
    background: transparent;
    padding: 6px;
}

/* Each day is a vertical column; days scroll horizontally */
.day-block {
    flex: 0 0 340px;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: linear-gradient(180deg,#ffffff,#fbfcfe);
    border: 1px solid rgba(15,23,36,0.04);
    border-radius: 12px;
    padding: 12px;
    box-shadow: 0 8px 24px rgba(15,23,36,0.06);
}

.day-header {
    font-weight: 700;
    margin: 0;
    padding-bottom: 6px;
    border-bottom: 1px solid rgba(15,23,36,0.03);
    color: #10243a;
    font-size: 0.95rem;
}

/* The hours list is vertically scrollable inside each day column */
.hours {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-top: 10px;
    overflow-y: auto;
    overflow-x: hidden;
    max-height: 60vh;
    padding-right: 6px; /* room for scrollbar */
}

.hour-row {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 10px 12px;
    border-radius: 10px;
    background: linear-gradient(180deg,#ffffff,#fbfdff);
    border: 1px solid rgba(15,23,36,0.04);
    width: 100%;
    box-sizing: border-box;
    transition: transform 140ms ease, box-shadow 140ms ease, background 160ms ease;
}

.hour-row:hover { transform: translateY(-3px); box-shadow: 0 10px 30px rgba(15,23,36,0.06); }

.hour-row:active { transform: translateY(0px) scale(0.997); box-shadow: 0 6px 16px rgba(15,23,36,0.04) inset; }

/* Time-of-day indicator */
.time-indicator {
    width: 12px;
    height: 32px;
    border-radius: 8px;
    flex: 0 0 12px;
    margin-right: 8px;
    box-shadow: inset 0 0 0 1px rgba(255,255,255,0.02);
}
.time-indicator.morning { background: linear-gradient(180deg,#fff6e6,#ffd98a); }
.time-indicator.day { background: linear-gradient(180deg,#fff5d6,#ffd24d); }
.time-indicator.evening { background: linear-gradient(180deg,#ffe3d2,#ffb07a); }
.time-indicator.night { background: linear-gradient(180deg,#233659,#122032); }

.hour-time { width: 56px; font-size: 0.95rem; color: var(--muted); }

/* Viewport and track for horizontal day scrolling */
.forecast-wrapper {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.forecast-viewport {
    overflow: hidden; /* hide native scrollbars */
    margin: 0 40px; /* space for nav buttons */
    box-sizing: border-box;
}

.days-track {
    display: flex;
    gap: 12px;
    transition: transform 320ms ease;
    will-change: transform;
}

.day-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid #ddd;
    background: #fff;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    box-shadow: 0 1px 2px rgba(0,0,0,0.06);
}

.day-nav.left { left: 6px; }
.day-nav.right { right: 6px; }

.day-nav[disabled] { opacity: 0.45; cursor: default; }

/* Simple clickable day tabs used to select a single day's hours */
.day-tabs {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 6px;
    margin-bottom: 8px;
}

.day-tab {
    padding: 6px 10px;
    background: #fff;
    border: 1px solid #e6eef6;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 600;
}

.day-tab.selected {
    background: var(--primary-color);
    color: white;
    border-color: var(--primary-color);
}

.single-day {
    display: block;
}

.hour-time { width: 56px; font-size: 0.9rem; }
.hour-icon { width: 28px; text-align: center; font-size: 1.2rem; }
.hour-temp { width: 56px; font-weight: 600; }
.hour-precip { font-size: 0.85rem; color: #555; }

.precip-icon, .snow-icon {
    display: inline-block;
    margin-right: 6px;
    font-size: 0.95rem;
    vertical-align: middle;
}

.hour-precip span { color: var(--muted); }

.no-data { color: #888; }

